<?php

use AwardWallet\MainBundle\Service\WatchdogKillListProvider;

$schema = "providerStatus";

require "start.php";

drawHeader("Provider errors", "");
global $Interface;
// Box for asking UserIDs
$Interface->drawAddUserIDsBox();

$providerId = (int) $_GET['ID'];
$showWithErrors = (int) ArrayVal($_GET, 'KE', 0);
$limit = (int) ArrayVal($_GET, 'L', 15);
$hideKilled = (int) ArrayVal($_GET, 'HK', 0);

if ($limit == 0 || $limit > 300) {
    $limit = 15;
}

$qProv = new TQuery("
    select 
        p.ProviderID, p.DisplayName, p.State, p.Code, p.Kind, p.InternalNote, p.WSDL, p.Assignee,
        p.AutoLogin, p.DeepLinking, p.CanCheckBalance, p.Corporate, p.CanCheckExpiration, p.CanCheckItinerary,
        p.CanCheckConfirmation, p.Tier, p.Severity, p.ResponseTime, p.Warning, u.Login as AssigneeLogin, p.StatePrev,
        p.CheckInBrowser, p.CheckInMobileBrowser, p.PasswordRequired
    from 
        Provider p 
        left outer join Usr u on p.Assignee = u.UserID
    where p.ProviderID = {$providerId}
");

assert(!$qProv->EOF);

/** @var WatchdogKillListProvider $watchdogKillListProvider */
$watchdogKillListProvider = $container->get(WatchdogKillListProvider::class);
$killedAccounts = $watchdogKillListProvider->search($limit, $providerId);
$imgFixed = "data:image/png;base64,";
$providerStatistics = getProviderStatistics($providerId);
$operationsImage = "<img src=\"data:image/png;base64,\" width=\"26\" height=\"20\" alt=\"  \"/>";
$copyProviderCode = "<button style='font-size:60%; padding:8px 7px 4px 0; vertical-align:top; background:none; border:none; color:gray; cursor:pointer;' onclick='copyText(\"#providerCode\")'>&nbsp; Copy</button>";
$escapedDisplayName = fixQuotes($qProv->Fields['DisplayName']);
$assignee = " <a class='clear' stateprev='" . (empty($qProv->Fields["StatePrev"]) ? '' : $arProviderState[$qProv->Fields["StatePrev"]]) . "' style='display: none' href='#' onclick=\"setAssignee({$providerId}, '', this.parentNode, '{$escapedDisplayName}'); return false;\"><img src='{$imgFixed}' title='Mark as fixed' style='float:left;padding-right:5px' height='20px'></a>";
$assignee .= " <a class='set' style='display: none' href='#' onclick=\"setAssignee({$providerId}, {$_SESSION['UserID']}, this.parentNode, '{$escapedDisplayName}'); return false;\">Mark as broken</a>";
$assignee .= "<span class='name' style='display: none' userId='{$qProv->Fields['Assignee']}'>Assignee: <span>{$qProv->Fields['AssigneeLogin']}</span></span>";
$extCheckIndicator = '';

if (
    $qProv->Fields['CheckInBrowser'] == CHECK_IN_MIXED
    || $qProv->Fields['CheckInMobileBrowser'] == 1
) {
    $extCheckText = '';

    if (
        $qProv->Fields['CheckInBrowser'] == CHECK_IN_MIXED
        && $qProv->Fields['CheckInMobileBrowser'] == 1
    ) {
        $extCheckText = 'desktop + mobile';
    } elseif ($qProv->Fields['CheckInBrowser'] == CHECK_IN_MIXED) {
        $extCheckText = 'desktop';
    } elseif ($qProv->Fields['CheckInMobileBrowser'] == 1) {
        $extCheckText = 'mobile';
    }
    $extCheckIndicator = "<i id='icon-ext' title='{$extCheckText}'></i>";
}
$displayName = $qProv->Fields['DisplayName'];

if (mb_strlen(html_entity_decode($displayName, ENT_QUOTES, "UTF-8")) > 40) {
    $displayName = mb_substr($displayName, 0, 40) . '...';
}
echo "
    <div class='providerStatus assignee'>{$assignee}</div>
    <h1 class='providerStatus' style='float:left; margin-top:5px;'>
        <a target='_blank' href='/manager/account-by-region?search=1&login2=&login3=&UnusedID={$providerId}&providerID={$providerId}&errorCode=" . ACCOUNT_ENGINE_ERROR . "' title='All unknown errors'>Errors</a> for provider <a id='providerCode' target='_blank' href='/manager/list.php?Schema=Provider&ProviderID={$providerId}' title='Provider settings'>{$qProv->Fields['Code']}</a> {$extCheckIndicator}
        {$providerStatistics} {$copyProviderCode}
    </h1>
    <div style='color:lightgray; font-size:180%; float:left; margin-top:6px;'>
        [{$displayName} / {$arProviderKind[$qProv->Fields["Kind"]]} |
            <nobr>
            <a target='_blank' href='/manager/list.php?ProviderPropertyID=&ProviderID={$providerId}&s1.x=6&s1.y=6&Name=&Code=&Required=&SortIndex=&Kind=&Visible=&Schema=ProviderProperty' style='color:lightgray; text-decoration:none;' title='Provider properties'>Properties</a>]
        <a target='_blank' href='/manager/operations?ID={$providerId}' style='color:lightgray; text-decoration:none;' title='Operations'>{$operationsImage}</a>
        </nobr>
    </div>
    <div style='clear:both;'></div>
";

$sql = "
    select UpdateDate, AccountID, UserID, Login, Login2, ErrorCode, ErrorMessage, DebugInfo, Balance, SavePassword, CheckedBy,
        Login3, SuccessCheckDate, ExpirationDate, ExpirationAutoSet, SubAccounts, DisableExtension, DisableClientPasswordAccess, PassChangeDate, CreationDate
	from Account where ProviderID = $providerId
    and State >= " . ACCOUNT_DISABLED . "
	and ErrorCode = " . ACCOUNT_ENGINE_ERROR . "
	and CheckedBy <> " . \AwardWallet\MainBundle\Entity\Account::CHECKED_BY_EMAIL . "
	and UpdateDate > DATE_SUB(NOW(), INTERVAL 1 DAY)
	order by UpdateDate desc
    limit {$limit}
";
$rows = SQLToArray($sql, "AccountID", "AccountID", true);
$unknownErrors = count($rows);

// show accounts with known errors
$showErrorsText = 'known errors';

if ($showWithErrors) {
    $showErrorsText = 'all errors';
}

if ((count($rows) < $limit) || $showWithErrors) {
    if ($showWithErrors) {
        $unknownErrorsLimit = $limit;
    } else {
        $unknownErrorsLimit = $limit - count($rows);
    }
    $sql = "
        select UpdateDate, AccountID, UserID, Login, Login2, ErrorCode, ErrorMessage, DebugInfo, Balance, SavePassword, CheckedBy,
            Login3, SuccessCheckDate, ExpirationDate, ExpirationAutoSet, SubAccounts, DisableExtension, DisableClientPasswordAccess, PassChangeDate, CreationDate
        from Account where ProviderID = $providerId
        and State >= " . ACCOUNT_DISABLED . "
        and ErrorCode <> " . ACCOUNT_CHECKED . " and ErrorCode <> " . ACCOUNT_ENGINE_ERROR . "
        and CheckedBy <> " . \AwardWallet\MainBundle\Entity\Account::CHECKED_BY_EMAIL . "
        and UpdateDate > DATE_SUB(NOW(), INTERVAL 1 DAY)
        order by UpdateDate desc
        limit {$unknownErrorsLimit}
    ";
    $unknownRows = SQLToArray($sql, "AccountID", "AccountID", true);

    if ($showWithErrors) {
        $rows = $unknownRows;
    } else {
        $rows = array_merge($rows, $unknownRows);
    }
}// if ((count($rows) < $limit) || $showWithErrors)

if ($hideKilled === 1) {
    $showKilledText = 'show';
} else {
    $showKilledText = 'hide';
}

$hide = false;
$allErrors = [];

if (is_array($killedAccounts) && count($killedAccounts) > 0) {
    $killedAccountIds = [];

    foreach ($killedAccounts as &$killedAccount) {
        $killedAccount['ErrorCode'] = null;
        $killedAccount['ErrorMessage'] = "Killed";
        $killedAccountIds[] = $killedAccount['AccountID'];
    }

    $otherErrors = [];
    $unknownAndKilledErrors = $killedAccounts;

    foreach ($rows as $row) {
        if (!in_array($row['AccountID'], $killedAccountIds) && ((int) $row['ErrorCode'] === ACCOUNT_ENGINE_ERROR || $row['ErrorMessage'] === 'Killed')) {
            $unknownAndKilledErrors[] = $row;
        } elseif ((int) $row['ErrorCode'] !== ACCOUNT_ENGINE_ERROR && $row['ErrorMessage'] !== 'Killed') {
            $otherErrors[] = $row;
        }
    }

    foreach ($unknownAndKilledErrors as $key => $row) {
        $updateDate[$key] = strtotime($row['UpdateDate']);
    }
    array_multisort($updateDate, SORT_DESC, $unknownAndKilledErrors);
    $allErrors = array_merge($unknownAndKilledErrors, $otherErrors);
    $allErrors = array_slice($allErrors, 0, $limit);

    $unknownErrs = 0;

    foreach ($allErrors as $row) {
        if ($hideKilled === 0 && ($row['ErrorMessage'] === 'Killed' || (int) $row['ErrorCode'] === ACCOUNT_ENGINE_ERROR)) {
            $hide = true;
            $unknownErrs++;
        }
    }

    if ($hideKilled === 1) {
        $hide = true;
    }
}

$showKilledLink = "";

if ($hide) {
    $showKilledLink = "<a href=\"/manager/providerErrors.php?ID={$providerId}&HK=" . ($hideKilled === 0 ? 1 : 0) . "&L={$limit}\">$showKilledText killed</a> / ";
}

if ($hideKilled === 0 && count($allErrors) > 0 && $showWithErrors === 0) {
    $rows = $allErrors;
    $unknownErrors = $unknownErrs;
}

if ($showWithErrors === 1) {
    $unknownErrors = 0;
}

if (count($rows) == 0) {
    echo "No accounts";
} else {
    echo "<span class='providerStatus'>" . $unknownErrors . " out of " . count($rows) . " errors " . ($unknownErrors == 1 ? "is" : "are") . " unknown ($showKilledLink<a href=\"/manager/providerErrors.php?ID={$providerId}&KE=" . ($showWithErrors == 0 ? 1 : 0) . "&L={$limit}\">show {$showErrorsText}</a>
	 / errors on page: <a href=\"/manager/providerErrors.php?ID={$providerId}&KE={$showWithErrors}&L=15&HK=" . $hideKilled . "\">15</a>, <a href=\"/manager/providerErrors.php?ID={$providerId}&KE={$showWithErrors}&L=30&HK=" . $hideKilled . "\">30</a>, <a href=\"/manager/providerErrors.php?ID={$providerId}&KE={$showWithErrors}&L=50&HK=" . $hideKilled . "\">50</a>, <a href=\"/manager/providerErrors.php?ID={$providerId}&KE={$showWithErrors}&L=100&HK=" . $hideKilled . "\">100</a>, <a href=\"/manager/providerErrors.php?ID={$providerId}&KE={$showWithErrors}&L=200&HK=" . $hideKilled . "\">200</a>)</span>";

    echo "<table class = 'detailsTable providerStatus' id='1' cellpadding = 4>
	<tr>";

    foreach ($rows[0] as $fieldName => $value) {
        if (!in_array($fieldName, ['UpdateDate', 'AccountID', 'UserID', 'Login', 'Login2', 'ErrorCode', 'ErrorMessage', 'DebugInfo', 'Balance', 'SavePassword', 'CheckedBy'])) {
            continue;
        }
        echo "<td><b>{$fieldName}</b></td>";
    }
    echo "<td><p style='text-align: right; margin: 0; padding: 0'><span id=\"select1\"><a id=\"selectAll\" href=\"javascript:selectAll(1)\">Select all</a> / <a id=\"selectUE\" href=\"javascript:selectUE()\">unknown</a></span><a style=\"display: none;\" id=\"unselect1\" href=\"javascript:unselectAll(1)\">Unselect all</a></p></td>";
    echo "<td><p style='text-align: right; margin: 0; padding: 0'><a href=\"javascript:checkAccounts()\">Check</a></p></td>";
    echo "</tr>";

    foreach ($rows as $row) {
        if ($row['CheckedBy'] != '') {
            $row['CheckedBy'] = \AwardWallet\MainBundle\Entity\Account::CHECKED_BY_NAMES[$row['CheckedBy']];
        }

        // workaround extDebuginfo -> src/wsdl/WsdlService.php line 1173
        if (stristr($row['DebugInfo'], 'ParseItineraries')) {
            $row['DebugInfo'] = preg_replace('/\,?\s*ParseItineraries:\s*\d+\,\s*ParseHistory:\s*\d+/', "", $row['DebugInfo']);
        }

        if (strpos($row['DebugInfo'], "\n") !== false) {
            $row['DebugInfo'] = "<ul><li>" . str_replace(["\n", "<li></li>", "/www/loyalty/current/src/AppBundle/Engine/", "/www/loyalty/current/vendor/awardwallet/", "/www/loyalty/current/vendor/facebook/"], ["</li>\n<li>", "", "", "", ""], $row['DebugInfo']) . "</li></ul>";

            $replace = [
                ',"profile.managed_default_content_settings.images"' => ', "profile.managed_default_content_settings.images"',
                ',"download.default_directory"' => ', "download.default_directory"',
                ',"profile.password_manager_enabled"' => ', "profile.password_manager_enabled"',
                ',"webrtc.nonproxied_udp_enabled"' => ', "webrtc.nonproxied_udp_enabled"',
                ',"webrtc.multiple_routes_enabled"' => ', "webrtc.multiple_routes_enabled"',
                ',"credentials_enable_service"' => ', "credentials_enable_service"',
                ',"profile.default_content_settings.popups"' => ', "profile.default_content_settings.popups"',
            ];
            $row['DebugInfo'] = str_replace(array_keys($replace), array_values($replace), $row['DebugInfo']);
        }

        if ($row['ErrorMessage'] !== 'Killed' && strpos($row['DebugInfo'], "<li></li>") !== false) {
            $row['DebugInfo'] = '';
        }

        if (!empty($row['DebugInfo'])) {
            $replace = [
                "/\"firefox_profile\":\"([^\"$]+)/ms" => '"firefox_profile":" ... ',
                '/"profile"\s*:\s*"([^\"]+)/ms' => '"profile": ...',
                "/\"extensions\":\s*\[\"([^]]+)/ms" => '"extensions":[" ... ',
            ];
            $row['DebugInfo'] = preg_replace(array_keys($replace), array_values($replace), $row['DebugInfo']);

            // reduce logs in DebugInfo with "ThrottledException: ..."
            preg_match_all("#ThrottledException:\s*Throttled at service\/old\/browser\/RequestThrottler\.php:59#ims", $row['DebugInfo'], $throttledExceptions);
            $throttledExceptionCount = count($throttledExceptions[0]);

            if ($throttledExceptionCount > 1) {
                for ($throttledExceptionIndex = 0; $throttledExceptionIndex < 5; $throttledExceptionIndex++) {
                    preg_match_all("#ThrottledException:\s*Throttled at service\/old\/browser\/RequestThrottler\.php:59, try {$throttledExceptionIndex}#ims", $row['DebugInfo'], $exceptionCounts);
                    $exceptionCount = count($exceptionCounts[0]);

                    if ($exceptionCount > 1) {
                        $row['DebugInfo'] = preg_replace("#ThrottledException:\s*Throttled at service\/old\/browser\/RequestThrottler\.php:59, try {$throttledExceptionIndex}, delay: \d+ sec#ims", "ThrottledException: Throttled at service/old/browser/RequestThrottler.php:59, try {$throttledExceptionIndex} - <strong>x{$exceptionCount}</strong>", $row['DebugInfo'], 1);
                        $row['DebugInfo'] = preg_replace("#<li>\s*ThrottledException:\s*Throttled at service\/old\/browser\/RequestThrottler\.php:59, try {$throttledExceptionIndex}, delay: \d+ sec</li>#ims", "", $row['DebugInfo']);
                    }// if ($exceptionCount > 1)
                }// for ($throttledExceptionIndex = 0; $throttledExceptionIndex < 5; $throttledExceptionIndex++)
            }// if ($throttledExceptionCount > 0)
            // reduce logs in DebugInfo with "ThrottledException: ..."
        }
        $links = [];
        $noLocalPass = ($row['SavePassword'] == SAVE_PASSWORD_DATABASE && $qProv->Fields['State'] != PROVIDER_CHECKING_EXTENSION_ONLY);

        if ($noLocalPass || $qProv->Fields['PasswordRequired'] != 1) {
            $links[] = "<a href=\"/manager/checkAccount.php?ID={$row['AccountID']}\">Check</a>";
        }
        $links[] = "<a href=\"/manager/impersonate?UserID={$row['UserID']}&AutoSubmit&AwPlus=1\" target=\"_blank\">Impersonate</a>";
        $row['SavePassword'] = $SAVE_PASSWORD[$row['SavePassword']];

        if (schemaAccessAllowed("logs")) {
            $links[] = "<a href=\"/manager/loyalty/logs?AccountID={$row['AccountID']}&ShowLatest=1\">Logs</a>";
        }
        echo "<tr>";

        // account fields
        foreach ($row as $fieldName => $value) {
            if (!in_array($fieldName, ['UpdateDate', 'AccountID', 'UserID', 'Login', 'Login2', 'ErrorCode', 'ErrorMessage', 'DebugInfo', 'Balance', 'SavePassword', 'CheckedBy'])) {
                continue;
            }
            $formattedValue = htmlspecialchars($value);

            if ($fieldName == 'DebugInfo') {
                $formattedValue = str_replace(["&lt;li&gt;", "&lt;/li&gt;", "&lt;ul&gt;", "&lt;/ul&gt;", "&lt;strong&gt;", "&lt;/strong&gt;"], ["<li>", "</li>", "<ul style='margin:1%; padding: 1% 0 1% 7%;font-size: 11px;'>", "</ul>", "<strong>", "</strong>"], $formattedValue);
            //                $formattedValue = mb_substr($formattedValue, 0, 2000);
            } elseif ($fieldName == 'Login') {
                $link = "/manager/passwordVault/requestPassword.php?ID={$row['AccountID']}";
                $formattedValue = "<a title=\"Password request\" href=\"{$link}\" target='_blank'>" . $formattedValue . "</a>";
            } elseif ($fieldName == 'UpdateDate') {
                $accountInfo = "
                    <div style='display: none;'>
                        <table>
                            <tr>
                                <td lab='accountInfo{$row['AccountID']}'>
                                    <ul style='padding: 0; margin-left: 30px; margin-top: 0; margin-bottom: 2px; margin-rigth: 0;'>
                                        <li> Login: {$row['Login']} </li>
                                        <li> Login2: {$row['Login2']} </li>
                                        <li> Login3: {$row['Login3']} </li>
                                    </ul>
                                    <ul style='padding: 0; margin-left: 30px; margin-top: 10px; margin-bottom: 2px; margin-rigth: 0;'>
                                        <li> SuccessCheckDate: {$row['SuccessCheckDate']} </li>
                                        <li> UpdateDate: {$row['UpdateDate']} </li>
                                        <li> ExpirationDate: {$row['ExpirationDate']} </li>
                                        <li> ExpirationAutoSet: {$row['ExpirationAutoSet']} </li>
                                        <li> SubAccounts: {$row['SubAccounts']} </li>
                                    </ul>
                                    <ul style='padding: 0; margin-left: 30px; margin-top: 10px; margin-bottom: 2px; margin-rigth: 0;'>
                                        <li> DisableExtension: {$row['DisableExtension']} </li>
                                        <li> DisableClientPasswordAccess: {$row['DisableClientPasswordAccess']} </li>
                                    </ul>
                                    <ul style='padding: 0; margin-left: 30px; margin-top: 10px; margin-bottom: 2px; margin-rigth: 0;'>
                                        <li> PassChangeDate: {$row['PassChangeDate']} </li>
                                        <li> CreationDate: {$row['CreationDate']} </li>
                                    </ul>
                                </td>
                            </tr>
                        </table>
                    </div>
                ";
                $formattedValue = "<span rel='accountInfo{$row['AccountID']}' id='accountInfo'>{$row['UpdateDate']}</span> {$accountInfo}";
            }
            echo "<td>{$formattedValue}</td>";
        }
        echo "<td>" . implode(" | ", $links) . "</td>";
        $ec = ($row['ErrorMessage'] !== 'Killed') ? $row['ErrorCode'] : 'Killed';

        if ($noLocalPass) {
            echo "<td style='text-align: center'><label style='display: block; margin: 0; padding: 0; cursor: pointer;' for='checkbox{$row['AccountID']}'><input type='checkbox' value='{$row['AccountID']}' id='checkbox{$row['AccountID']}ec{$ec}' name='checkboxes[]' onclick='check(this)'></label></td>";
        } else {
            echo "<td></td>";
        }
        echo "</tr>";
    }
    echo "</table>";
}

echo "<h1>Internal Note:</h1>";
echo "<table class = 'detailsTable providerStatus' cellpadding = 4>
    <tr>";

if (!empty($qProv->Fields['InternalNote'])) {
    echo "<td>{$qProv->Fields['InternalNote']}</td>";
} else {
    echo "<td>No notes to display</td>";
}
echo "</tr>";
echo "</table>";

drawFooter();

function getProviderStatistics($providerId)
{
    global $watchdogKillListProvider;
    $response = [];
    $response[$providerId] = $watchdogKillListProvider->search(null, $providerId);
    $killedAccounts = [];
    $killedSql = "";
    $dateUtc = strtotime(gmdate('d M Y H:i:s'));
    $lastHourDate = strtotime('-1 hour', $dateUtc);
    $killedAccountsForLastHour = [];

    foreach ($response as $provId => $accounts) {
        foreach ($accounts as $account) {
            if (!isset($account['AccountID'])) {
                continue;
            }
            $killedAccounts[] = $account['AccountID'];

            if (strtotime($account['UpdateDate']) >= $lastHourDate) {
                if (!isset($killedAccountsForLastHour[$provId])) {
                    $killedAccountsForLastHour[$provId] = 1;
                } else {
                    $killedAccountsForLastHour[$provId]++;
                }
            }
        }
    }

    if (count($killedAccounts) > 0) {
        $killedSql = "and a.AccountID not in(" . implode(", ", $killedAccounts) . ")";
    }
    // get providers
    $sql = "
        select
            a.ProviderID,
            count(a.AccountID) as TotalCount,
            sum(case when a.ErrorCode = " . ACCOUNT_ENGINE_ERROR . " then 1 else 0 end) AS UnkErrors,
            sum(case when a.AccountID is not null and a.UpdateDate > adddate(now(), interval -4 hour) then 1 else 0 end) AS LastChecked,
            sum(case when a.AccountID is not null and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourChecked,
            sum(case when a.ErrorCode = " . ACCOUNT_ENGINE_ERROR . " and a.UpdateDate > adddate(now(), interval -4 hour) then 1 else 0 end) AS LastUnkErrors,
            sum(case when a.ErrorCode = " . ACCOUNT_ENGINE_ERROR . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourUnkErrors,
            sum(case when a.ErrorCode = " . ACCOUNT_CHECKED . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourSuccessfullyChecked,
            sum(case when a.ErrorCode = " . ACCOUNT_PROVIDER_ERROR . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourProviderErrors,
            sum(case when a.ErrorCode = " . ACCOUNT_INVALID_PASSWORD . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourInvalidPassword,
            sum(case when a.ErrorCode in (" . ACCOUNT_LOCKOUT . ", " . ACCOUNT_PREVENT_LOCKOUT . ") and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourLockouts,
            sum(case when a.ErrorCode = " . ACCOUNT_WARNING . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourWarnings,
            sum(case when a.ErrorCode = " . ACCOUNT_QUESTION . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourQuestions,
            sum(case when a.ErrorCode <> " . ACCOUNT_CHECKED . " then 1 else 0 end) AS Errors,
            sum(case when a.ErrorCode = " . ACCOUNT_CHECKED . " then 1 else 0 end) AS SuccessfullyChecked,
            sum(case when a.ErrorCode = " . ACCOUNT_PROVIDER_ERROR . " then 1 else 0 end) AS ProviderErrors,
            sum(case when a.ErrorCode = " . ACCOUNT_INVALID_PASSWORD . " then 1 else 0 end) AS InvalidPassword,
            sum(case when a.ErrorCode in (" . ACCOUNT_LOCKOUT . ", " . ACCOUNT_PREVENT_LOCKOUT . ") then 1 else 0 end) AS Lockouts,
            sum(case when a.ErrorCode = " . ACCOUNT_WARNING . " then 1 else 0 end) AS Warnings,
            sum(case when a.ErrorCode = " . ACCOUNT_QUESTION . " then 1 else 0 end) AS Questions,
            sum(case when a.ErrorCode <> " . ACCOUNT_CHECKED . " and a.UpdateDate > adddate(now(), interval -1 hour) then 1 else 0 end) AS LastHourErrors,
            round(sum(case when a.ErrorCode = " . ACCOUNT_ENGINE_ERROR . " then 1 else 0 end)/count(a.AccountID)*100, 2) AS ErrorRate,
            round(sum(case when a.ErrorCode = " . ACCOUNT_CHECKED . " then 1 else 0 end)/count(a.AccountID)*100, 2) AS SuccessRate
        from
            Account a
        where
            a.ProviderID = {$providerId}
            and a.CheckedBy <> " . \AwardWallet\MainBundle\Entity\Account::CHECKED_BY_EMAIL . "
            and a.UpdateDate > date_sub(now(), interval 1 day)
            and a.State not in (" . implode(', ', [ACCOUNT_PENDING, ACCOUNT_IGNORED]) . ")
            {$killedSql}
        group by
            a.ProviderID
    ";

    if (ConfigValue(CONFIG_SITE_STATE) === SITE_STATE_DEBUG) {
        $sql = str_replace(['1 hour', '4 hour', '1 DAY'], ['5000 hour', '5000 hour', '180 DAY'], $sql);
    }
    $q = new TQuery($sql); // UnkErrors desc, SuccessRate //p.wsdl desc, Tier, Severity DESC, TotalCount

    $rows = [];

    while (!$q->EOF) {
        if (isset($response[$q->Fields['ProviderID']]) && count($response[$q->Fields['ProviderID']]) > 0) {
            $q->Fields['UnkErrors'] += count($response[$q->Fields['ProviderID']]);
        }

        if ($q->Fields["TotalCount"] > 0) {
            $rows[] = $q->Fields;
        }
        $q->Next();
    }

    if (count($rows) > 0) {
        $row = $rows[0];
    } else {
        $total = 0;
        $killedForLastHour = 0;
        $row = [
            'ProviderID' => $providerId,
            // last hour
            'LastHourErrors' => 0,
            'LastHourProviderErrors' => 0,
            'LastHourInvalidPassword' => 0,
            'LastHourLockouts' => 0,
            'LastHourQuestions' => 0,
            'LastHourWarnings' => 0,
            'LastHourUnkErrors' => 0,
            'LastHourSuccessfullyChecked' => 0,
            'LastHourChecked' => 0,
            // total
            'TotalCount' => 0,
            'SuccessfullyChecked' => 0,
            'SuccessRate' => 0,
            'UnkErrors' => 0,
            'ProviderErrors' => 0,
            'InvalidPassword' => 0,
            'Lockouts' => 0,
            'Questions' => 0,
            'Warnings' => 0,
        ];
    }

    $killedAccs = 0;

    if (isset($response[$row['ProviderID']])) {
        $killedAccs = count($response[$row['ProviderID']]);
    }
    $lastHourCount = intval($row["LastHourUnkErrors"]);
    $total = intval($row["LastHourErrors"]);
    $killedForLastHour = 0;

    if (isset($killedAccountsForLastHour[$row['ProviderID']])) {
        $lastHourCount += $killedAccountsForLastHour[$row['ProviderID']];
        $total += $killedAccountsForLastHour[$row['ProviderID']];
        $killedForLastHour = $killedAccountsForLastHour[$row['ProviderID']];
    }

    $stats = "
        <i class='icon-blue-info' rel='Statistic{$row['ProviderID']}' id='Statistic'> </i>
        <div style='display: none;'>
            <table>
                <tr>
                    <td lab='Statistic{$row['ProviderID']}'>
                        <strong style='text-decoration: underline;'>Provider's statistic:</strong>
                        <br/>
                        Success rate: " . $row["SuccessRate"] . "%
                        <br/>
                        <br/>
                        <strong style='text-decoration: underline;'>For the last hour:</strong>
                        <br/>
                        Total errors: " . $total . "
                        <br/>
                        <ul style='padding: 0; margin-left: 30px; margin-top: 0; margin-bottom: 2px; margin-rigth: 0;'>
                        <li>
                            Provider errors: " . intval($row["LastHourProviderErrors"]) . "
                        </li>
                        <li>
                            Invalid credentials: " . intval($row["LastHourInvalidPassword"]) . "
                        </li>
                        <li>
                            Account lockouts: " . intval($row["LastHourLockouts"]) . "
                        </li>
                        <li>
                            Questions: " . intval($row["LastHourQuestions"]) . "
                        </li>
                        <li>
                            Warnings: " . intval($row["LastHourWarnings"]) . "
                        </li>
                        <li>
                            Unknown errors: " . intval($row["LastHourUnkErrors"]) . "
                        </li>
                        <li>
                            Killed: " . $killedForLastHour . "
                        </li>
                        </ul>
                        Successfully checked: " . intval($row["LastHourSuccessfullyChecked"]) . "
                        <br/>
                        Total checked: " . intval($row["LastHourChecked"]) . "
                        <br/>
                        <br/>
                        <strong style='text-decoration: underline;'>Total:</strong>
                        <br/>
                        Checked: " . intval($row["TotalCount"]) . "
                        <br/>
                        Successfully checked: " . intval($row["SuccessfullyChecked"]) . "
                        <br/>
                        Unknown errors: " . intval($row["UnkErrors"]) . "
                        <br/>
                        Provider errors: " . intval($row["ProviderErrors"]) . "
                        <br/>
                        Invalid credentials: " . intval($row["InvalidPassword"]) . "
                        <br/>
                        Account lockouts: " . intval($row["Lockouts"]) . "
                        <br/>
                        Questions: " . intval($row["Questions"]) . "
                        <br/>
                        Warnings: " . intval($row["Warnings"]) . "
                        <br/>
                        Killed: " . $killedAccs . "
                        <br/>
                    </td>
                </tr>
            </table>
        </div>
    ";

    return $stats;
}

